    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $false)]
        [string]
        $Username = $("{0}@{1}" -f $Env:USERNAME, $Env:USERDOMAIN),
        [Parameter(Mandatory = $false)]
        [string]
        $Date = (Get-Date -Format "MMM dd, yyyy"),
        [Parameter(Mandatory = $false)]
