function Format-Wrapped {
    param(
        # Text
        [Parameter(ValueFromPipeline = $true)]
        [string]
        $Text,
        # Text
        [Parameter(ValueFromPipeline = $false)]
        [int]
        $Length
    )

    $Lines = $Text.Split([System.Environment]::NewLine)
    $Result = Foreach ($Line in $Lines) {
        $Tokenized = $Line.Split(' ')
        $CurrentLine = ''
        $Tokenized.Foreach({
                if (($CurrentLine.Length + $_.Length + 1) -lt $Length) {
                    $CurrentLine += " $_"
                }
                else {
                    $CurrentLine
                    $CurrentLine = $_
                }
            })
        if ($CurrenLine.Length -lt $Length) {
            $CurrentLine
        }
    }
    return $Result.Trim()
}

function Box-Text {
    [CmdletBinding()]
    param (
        [Parameter()]
        [string[]]
        $Text
    )
    $Longest = 0
    $Text.Foreach({ if ($_.Length -gt $Longest) { $Longest = $_.Length } })
    $Longest += 2
    $BoxedText = New-Object System.Collections.ArrayList
    $BoxedText.Add('╭' + $('─' * $Longest) + '╮') | Out-Null
    $Text.Foreach({
            $EndPadding = ($Longest - $_.Length)
            $BoxedText.Add(( "│ $_" + (' ' * ($EndPadding - 1)) + '│' )) | Out-Null
        })
    $BoxedText.Add(( '╰' + $('─' * $Longest) + '╯' )) | Out-Null
    return [PSCustomObject]@{
        Text   = $BoxedText
        Width  = $Longest + 4
        Height = $BoxedText.Count 
    }
}

