    $MaxLineLength = $Host.UI.RawUI.WindowSize.Width - $Wagon.XOffset - 4
    $WrappedText = Format-Wrapped -Length $MaxLineLength -Text $UserCondition
    $Box = Box-Text -Text $WrappedText

    if ( -not $Weather) {
        $WeatherResult = Invoke-WebRequest -Uri "https://wttr.in/?format='%f,+%C'"
        $Weather = $WeatherResult.Content[2..($WeatherResult.Content.Length - 2)] -join ''
    }

    $CDrive = Get-Volume -DriveLetter C
    if ( -not $DiskCUsed) {
        $DiskCUsed = [Math]::Round( ($CDrive.Size - $CDrive.SizeRemaining) / 1GB)
    }
    if ( -not $DiskCFree) {
        $DiskCFree = [Math]::Round( $CDrive.SizeRemaining / 1GB)
    }
